import { useState } from 'react'
import { useCookies } from 'react-cookie'

const Modal = ({ mode, setShowModal, getData, task }) => {
  const [cookies, setCookie, removeCookie] = useCookies(null)
  // Decide if the modal is in 'edit' mode or 'create' mode
  const editMode = mode === 'edit' ? true : false

  // Initialize state for the task data
  const [data, setData] = useState({
    user_email: editMode ? task.user_email : cookies.Email, 
    title: editMode ? task.title : null, 
    progress: editMode ? task.progress : 50, 
    date: editMode ? task.date : new Date() 
  })

  // Function to send new task data to server
  const postData = async (e) => {
    e.preventDefault() // Prevent default form submission
    try {
      // Send a POST request to create a new task
      const response = await fetch(`${process.env.REACT_APP_SERVERURL}/todos`, {
        method: "POST",
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      })
      if (response.status === 200) {
        console.log('WORKED') // Task created successfully
        setShowModal(false) // Close the modal
        getData() // Fetch the updated task list
      }
    } catch(err) {
      console.error(err) // Handle error
    }
  }

  // Function to update existing task data on the server
  const editData = async(e) => {
    e.preventDefault() // Prevent default form submission
    try {
      // Send a PUT request to update the task
      const response = await fetch(`${process.env.REACT_APP_SERVERURL}/todos/${task.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      })
      if (response.status === 200) {
        setShowModal(false) // Close the modal
        getData() // Fetch the updated task list
      }
    } catch (err) {
      console.error(err) // Handle error
    }
  }

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target
    // Update the state with the changed input value
    setData(data => ({
      ...data,
      [name]: value
    }))

    console.log(data) // Log the updated task data (for debugging)
  }

  return (
    <div className="overlay">
      <div className="modal">
        <div className="form-title-container">
          <h3>Let's {mode} your task</h3> {/* Display 'edit' or 'create' based on mode */}
          <button onClick={() => setShowModal(false)}>X</button> {/* Close the modal */}
        </div>

        <form>
          {/* Input field for task title */}
          <input
            required
            maxLength={30}
            placeholder=" Your task goes here"
            name="title"
            value={data.title}
            onChange={handleChange} // Call handleChange on input change
          />
          <br />
          {/* Input field for task progress with a range slider */}
          <label for="range">Drag to select your current progress</label>
          <input
            required
            type="range"
            id="range"
            min="0"
            max="100"
            name="progress"
            value={data.progress}
            onChange={handleChange} // Call handleChange on input change
          />
          {/* Submit button to either create or edit the task */}
          <input className={mode} type="submit" onClick={editMode ? editData : postData} />
        </form>

      </div>
    </div>
  )
}

export default Modal
