import { useState, useEffect } from 'react';

const TickIcon = ({ taskId }) => {
  // Retrieve the completion status of the specific task from localStorage
  const [isCompleted, setIsCompleted] = useState(() => {
    const savedStatus = localStorage.getItem(`taskStatus_${taskId}`);
    return savedStatus ? JSON.parse(savedStatus) : false; // Default to false if not found
  });

  // Function to handle click event and toggle the completion status
  const handleClick = () => {
    const newStatus = !isCompleted; // Toggle the status
    setIsCompleted(newStatus); // Update the local state

    // Save the new completion status of the specific task in localStorage
    localStorage.setItem(`taskStatus_${taskId}`, JSON.stringify(newStatus));
  };

  // Set the color based on the completion status
  const iconColor = isCompleted ? 'rgb(141, 181, 145)' : 'rgb(255, 99, 71)'; // Green if completed, Red if not

  return (
    <svg
      className="tick"
      xmlns="http://www.w3.org/2000/svg"
      width="25"
      height="25"
      viewBox="0 0 512 512"
      onClick={handleClick} // Add the click handler
    >
      <title>ionicons-v5-e</title>
      <path 
        d="M448,256c0-106-86-192-192-192S64,150,64,256s86,192,192,192S448,362,448,256Z" 
        fill={iconColor} // Set the fill color of the circle
      />
      <polyline 
        points="352 176 217.6 336 160 272" 
        fill="none"
        stroke={iconColor} // Set the stroke (checkmark) color
        strokeWidth="30" // Make the checkmark line thicker
      />
    </svg>
  );
};

export default TickIcon;
