import { useState } from 'react';

const TickIcon = () => {
  const [isCompleted, setIsCompleted] = useState(false);

  // Function to handle click event and toggle the completion status
  const handleClick = () => {
    setIsCompleted(!isCompleted); 
  };

  // Set the color based on the completion status
  const iconColor = isCompleted ? 'rgb(141, 181, 145)' : 'rgb(255, 214, 161)'; // Green if completed, else the default color

  return (
    <svg
      className="tick"
      xmlns="http://www.w3.org/2000/svg"
      width="25"
      height="25"
      viewBox="0 0 512 512"
      onClick={handleClick} // Add the click handler
    >
      <title>ionicons-v5-e</title>
      <path 
        d="M448,256c0-106-86-192-192-192S64,150,64,256s86,192,192,192S448,362,448,256Z" 
        fill={iconColor} // Set the fill color of the circle
      />
      <polyline 
        points="352 176 217.6 336 160 272" 
        fill="none"
        stroke={iconColor} // Set the stroke (checkmark) color
        strokeWidth="30" // Make the checkmark line thicker
      />
    </svg>
  );
};

export default TickIcon;
